import React from "react";
// material-ui components
import { makeStyles } from "@material-ui/core/styles";
// core components
import Card from "./components/Card/Card.js";
import CardBody from "./components/Card/CardBody.js";
import CardMedia from '@material-ui/core/CardMedia';
import CardHeader from "./components/Card/CardHeader.js";
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';

import { cardTitle } from "./assets/jss/material-kit-react.js";

const styles = {
  cardTitle,
};

var loadFile = function(file) {
	file.src = URL.createObjectURL(file);

    return file
};

const useStyles = makeStyles(styles);

export default function MomentCard(props) {
  const classes = useStyles();
  loadFile(props.moment.image)

  return (
    <Card style={{width: "20rem", }}>
      <CardHeader style={{
          color: 'white',
          backgroundColor: props.moment.color
      }}>Featured</CardHeader>
      <CardBody>
        <h4 className={classes.cardTitle}>{props.moment.message}</h4>
        <p>
          {props.moment.message}
        </p>
        <IconButton color="primary"
            onClick={(e) => props.globalActions.removeMoment(e.target.id)}>
          <RemoveIcon id={props.moment.message}/>
        </IconButton>
        <CardMedia style={{height: 100}} component="img" src={props.moment.image.src} />
      </CardBody>
    </Card>
  );
}
