import React from 'react';
import logo from './logo.svg';
import globalHook from 'use-global-hook';
import actions from './actions.js'
import MomentCard from './MomentCard.js'
import CreateMoment from './CreateMoment.js';
import IconButton from '@material-ui/core/IconButton';
import AddIcon from '@material-ui/icons/Add';
import './App.css';

const initialState = {
  moments: []
};

console.log("Begin")
console.log(initialState)
console.log(initialState.moments)
console.log("Done")

const useGlobal = globalHook(React, initialState, actions);

function App() {
    const [globalState, globalActions] = useGlobal();
  return (
    <div className="App">
      <header className="App-header">
        {globalState.moments.map((moment) => {
            return <MomentCard key={moment.message} moment={moment}/>
        })}

        <CreateMoment globalState={globalState} globalActions={globalActions}/>
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
