import React from 'react';
import './App.css';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';

class MomentCard extends React.Component {
    state = {
        active: false
    }

    useStyles() {
        return makeStyles({
          root: {
            minWidth: 275,
          },
          bullet: {
            display: 'inline-block',
            margin: '0 2px',
            transform: 'scale(0.8)',
          },
          title: {
            fontSize: 14,
          },
          pos: {
            marginBottom: 12,
          },
        });
    }

    render() {
        const classes = this.useStyles();

        if (this.state.active) {
            return (
                <div></div>
            )
        }

        return (
            <Card className={classes.root}>
                <Typography variant="h5" component="h2">
                  This should be the title of my card
                </Typography>
                <Typography variant="body2" component="p">
                  And this should be the body theoretically
                </Typography>
                <Typography variant="body2" component="p">
                  What happens if I add another layer of text
                </Typography>
            </Card>
        )
    }
}

export default MomentCard;
